package week1.day1;

public class ConvertNegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -40;
		int x;
		if (number < 0) {
			x = number * (-1);
			System.out.println("Positive number :" + x);
			System.out.println("The number -40 is converted to 40");
		}

	}

}
